Tracker.autorun(function(){
  Meteor.subscribe('company');
});


Template.claimApproval.helpers({
  approvalLetterSchema: function() {
    return Schema.approvalLetter;
  }
});

Template.claimApproval.events({
    'submit #approvalLetterForm': function(event) {
      var today = new Date().toDateString();
      var company = event.target.company.value;
      var address = event.target.address.value;
      var name = event.target.insuredName.value;
      var claimNumber = event.target.refNumber.value;

      // Define the pdf-document
      var docDefinition = {
        pageSize: 'A4',
        pageMargins: [ 30, 25, 30, 25 ],
      // Content with styles
        content: [
          {
            image: 'data:image/jpeg;base64,',
            width: 150
          },
          {
            text: ['Date: ','today'],
            style: 'date'
          },
          {
            stack: [
          		company,
              {text: address, style: 'subheader'},
            ],
              style: 'address'
          },
          {
          	text:'Subject: Letter of Approval for Client Benefit',
          	style: 'body',
          	bold: false
          },
          {

          	text: ['Dear ', 'company'],
            style:'greeting',
          	bold: false

          },
          {
            text: 'We are pleased to inform you that the payment of the following claim has been approved:',
            style: 'address',
            bold: false
          },
          {
          	style: 'tableData',
          	table: {
          		body: [
          			['Claim Number:', 'claimNumber'],
          			['Name of Insured: ', 'name'],
                ['Client Type: ', 'clientType'],
          			['Coverage Period: ', 'December 01, 2014 – June 01, 2015'],
                ['Date of Death: ', 'deathDate'],
          			['Coverage Type and Amount: ', 'Life P50,000 (Assistance)'],
                ['Beneficiary:', 'Azarcon, Magdalena Salamat'],
          			['Relationship to Insured:', 'Principal - Spouse'],
          		]
          	}
          },
          {
          	text: 'Also we wish to ensure proper settlement of this claim, kindly return the signed check voucher and deed of settlement to us.',
          	style: 'address',
          	bold: false
          },
          {
          	text: 'Sincerely Yours,',
          	style: 'signature',
          	bold: false
          },
          {
            image: 'data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/2wBDAAgGBgcGBQgHB,',
            width: 150
          },
          {
            text:'Caleb P. Jimenez',
            style: 'signature',
          },
          {
            text:['President & CEO\n',
            'Pinoy Ako Insurance Services'],
            style: 'position'
          },
          {
            text:[ 'Pinoy Ako Insurance Services\n',
            '157A K-6th Street East Kamias, Anonas, Quezon City Philippines\n',
            'Tel: +63 2 4348257'],
            style: 'footer'
          }
        ],
        styles: {
        	address: {
        	  fontSize: 11,
            bold: true,
            margin: [0,30,0,30]
          },
          date: {
            fontSize: 10,
          	alignment: 'right',
            margin: [0,30,0,30]
          },
          subheader: {
            fontSize:10,
            bold: false,
          },
          body: {
            fontSize:10,
          },
          signature: {
            fontSize:10,
          },
            position: {
            italics: true
          },
          greeting: {
            fontSize: 10,
            bold: true,
            margin: [0, 30, 0, 15]
          },
          tableData: {
            fontSize: 11,
            margin: [0, 5, 0, 15]
          },
          footer: {
            fontSize: 9,
            alignment:'center',
            margin: [0, 80, 0, 15]
          }
        }
      };
      // Start the pdf-generation process
      pdfMake.createPdf(docDefinition).open();

      // clear form & prevent browser refresh
      //event.target.name.value = "";
      event.preventDefault();
    }
});
